mapper.select:
SELECT * FROM t_allocation 
WHERE resource_id = #{resourceId}
ORDER BY start_time DESC

SELECT * FROM t_allocation
WHERE resource_id = #{resourceId}
  AND status = 'ACTIVE'
  AND start_time < #{endTime}
  AND end_time > #{startTime}
ORDER BY start_time ASC

SELECT * FROM t_allocation 
WHERE project_id = #{projectId}
ORDER BY start_time DESC

SELECT * FROM t_allocation
WHERE resource_id = #{resourceId}
  AND status = 'ACTIVE'
  AND start_time < #{endTime}
  AND end_time > #{startTime}

SELECT * FROM t_allocation
WHERE resource_id = #{resourceId}
  AND id != #{excludeId}
  AND status = 'ACTIVE'
  AND start_time < #{endTime}
  AND end_time > #{startTime}
